DROP FUNCTION IF EXISTS norpac_commons.i_customer;
CREATE FUNCTION norpac_commons.i_customer(
  IN p_id_tenant UUID, 
  IN p_customer_id VARCHAR, 
  IN p_business_name VARCHAR, 
  IN p_first_name VARCHAR, 
  IN p_last_name VARCHAR, 
  IN p_email VARCHAR, 
  IN p_phone_number VARCHAR, 
  IN p_preferred_contact VARCHAR, 
  IN p_date_of_birth DATE, 
  IN p_description TEXT, 
  IN p_created_by VARCHAR
)
RETURNS norpac_commons.pg_resp
AS $$
DECLARE

  c_service_name TEXT := 'i_customer';

  v_metadata     JSONB := '{}'::JSONB;
  v_errors       JSONB := '[]'::JSONB;
  v_val_resp     norpac_commons.pg_val;  
  v_response     norpac_commons.pg_resp;
  v_updated_at   TIMESTAMP;
  
  -- Primary Key Field(s)
  v_id uuid := NULL;

BEGIN

  -- ------------------------------------------------------
  -- Metadata
  -- ------------------------------------------------------

  v_metadata := jsonb_build_object(
    'id_tenant', p_id_tenant, 
    'customer_id', p_customer_id, 
    'business_name', p_business_name, 
    'first_name', p_first_name, 
    'last_name', p_last_name, 
    'email', p_email, 
    'phone_number', p_phone_number, 
    'preferred_contact', p_preferred_contact, 
    'date_of_birth', p_date_of_birth, 
    'description', p_description, 
    'created_by', p_created_by
  );
  
  -- ------------------------------------------------------
  -- Validations
  -- ------------------------------------------------------
  
  v_val_resp := is_email('email', p_email);
  IF NOT v_val_resp.passed THEN
    v_errors := v_errors || jsonb_build_object('type', 'validation', 'field', v_val_resp.field, 'message', v_val_resp.message);
  END IF;

  IF jsonb_array_length(v_errors) > 0 THEN
    v_response := (
      'ERROR', 
      NULL, 
      v_errors, 
      '23514', 
      'A CHECK constraint was violated due to incorrect input', 
      'Ensure all fields in the ''errors'' array are correctly formatted', 
      'The provided data did not pass validation checks'
    );
    CALL norpac_commons.i_logs(v_response.status, v_response.message, c_service_name, p_created_by, v_metadata);
    RETURN v_response;
  END IF;
  
  -- ------------------------------------------------------
  -- Persist
  -- ------------------------------------------------------
 
  INSERT INTO norpac_commons.customer (
    id_tenant, 
    customer_id, 
    business_name, 
    first_name, 
    last_name, 
    email, 
    phone_number, 
    preferred_contact, 
    date_of_birth, 
    description, 
    created_by,
    updated_by
  )
  VALUES (
    p_id_tenant, 
    p_customer_id, 
    p_business_name, 
    p_first_name, 
    p_last_name, 
    p_email, 
    p_phone_number, 
    p_preferred_contact, 
    p_date_of_birth, 
    p_description, 
    p_created_by,
    p_created_by
  )
  RETURNING id, updated_at INTO v_id, v_updated_at;

  v_response := (
    'OK',
    jsonb_build_object('id', v_id, 'updated_at', v_updated_at), 
    NULL, 
    '00000',
    'Insert was successful', 
    NULL, 
    NULL
  );
  RETURN v_response;

  -- ------------------------------------------------------
  -- Exceptions
  -- ------------------------------------------------------
  
  EXCEPTION
    WHEN UNIQUE_VIOLATION THEN
      v_response := (
        'ERROR', 
        NULL, 
        jsonb_build_object('type', 'database', 'message', 'A UNIQUE constraint was violated due to duplicate data'), 
        '23514', 
        'A UNIQUE constraint was violated due to duplicate data', 
        'A record already exists in the customer table', 
        'Check the provided data and try again'
      );
      CALL norpac_commons.i_logs(v_response.status, v_response.message, c_service_name, p_created_by, v_metadata);
      RETURN v_response;
  
    WHEN OTHERS THEN
      v_response := (
        'ERROR', 
        NULL, 
        jsonb_build_object('type', 'database', 'message', SQLERRM), 
        SQLSTATE, 
        'An unexpected error occurred', 
        'Check database logs for more details', 
        SQLERRM
      );
      CALL norpac_commons.i_logs(v_response.status, v_response.message, c_service_name, p_created_by, v_metadata);
      RETURN v_response;
  
END;
$$ LANGUAGE plpgsql;
